-- Structure de la table `story`
--

CREATE TABLE `story` (
  `id` int(11) NOT NULL,
  `title` varchar(2555) DEFAULT NULL,
  `issue_id` int(11) DEFAULT NULL,
  `synopsis` text,
  `reprint_notes` text,
  `notes` text,
  `type_id` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

