    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22642,"Captain America Battles The Camera Fiend and His Darts of Doom", 1639,"Cap and Bucky along with the Sentinels of Liberty face an arch villain who uses his camera that fires poisoned needles at anyone who stands in his way of securing the Crown Jewels arriving in America from England.",NULL,"Pencil credits from Greg Theakston. Much info and synopsis added by Craig Delich 1-4-09.",19
