    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22643,"Meet the Fang Arch-Fiend of the Orient", 1639,"Cap and Bucky battle against the evil Fang and Baron Nushima who seek to prevent two emissaries from China from obtaining money loans from the American government.",NULL,"Pencil credits from Greg Theakston. Much info and synopsis added by Craig Delich 1-4-09.",19
