    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22644,"The Grim Reaper Deals with Crime", 1639,"Because he was delayed by crooks guilty of a crime Larry Scott's father dies for that crime and so he decides to make time also work for the forces of law and order...as Father Time!",NULL,"Much info and synopsis added by Craig Delich 1-4-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22645,"The Strange Case of Captain America and The Hangman: Who Killed Doctor Vordoff", 1639,"Cap and Bucky get on a merry-go-round on intrigue and murder in order to find the supposed murderer of a Dr. Vardoff who has created a single strand of silken thread as strong as steel.",NULL,"Pencil credits from Greg Theakston. Much info and synopsis added by Craig Delich 1-4-09.",19
