    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22646,"Trap for a Traitor", 1639,"Bucky glimpses a man whom he and Cap had thrown into prison a few months back following him and not wanting to allow him to see that Steve Rogers and Captain America were one and the same person decides to enlist the aid of the Sentinels of Liberty member to warn Cap of what was happening.",NULL,"Art credits synopsis and other info added by Craig Delich 1-4-09.",21
