    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22647,"The Engine of Destruction", 1639,"While searching for a story on the outskirts of London Jerry Hunter runs across a mysterious factory where a secret weapon is supposedly being produced. Knowing that the owner was a former Fascist he investigates the place and discovers it to be a Nazi stronghold.",NULL,"Much info and synopsis added by Craig Delich 1-4-09.",19
