    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22648,"The Boarding House Murders", 1639,"Hurricane is traveling the streets of New York seeking lodging for the night but a small girl warns him not to seek a room at a near-by boarding house since two mysterious murders have occurred there.",NULL,"Much info and synopsis added by Craig Delich 1-4-09.",19
