    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22650,"Highway of Mystery", 1640,"N-4 a Nazi saboteur has infiltrated the Topper Tank factory as an executive secretary. She has her men build a hideout above a superhighway so that she can drop a ramp to capture experimental models of a new tank during transit. She is finally unmasked by the Torch and Toro.",NULL,NULL,19
