    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22652,"Swastika Over Europe", 1640,"The Patriot puts an end to the murderous activity of a self-confessed Bundsman who wanted to end a play that was anti-Nazi.",NULL,"The Masterworks edition credits Alan Mandel with the art.",19
