    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22654,"Cult of the Shark-god", 1640,"The Vision comes to Earth to investigate the sacrificing of humans to the mysterious Kai-Mak the Shark-God.",NULL,"The Masterworks edition credits Jack Kirby alone for story and pencils but also credits an unknown inker assisting Joe Simon.",19
