    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(22655,"Neutralizing the Nazis", 1640,"Ka-Zar ends his battle with the Nazis who are attempting to establish an air base in the African jungle.",NULL,"The title of this strip is \"Adventures of Ka-Zar the Great\". A ? has been placed by the writer. The Who's Who indicates that Thompson never wrote any of the features he illustrated for Timely.",19
