    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23090,"U.S. military secrets mysteriously sent to Germany help the master spy.....", 1678,"The Arrow assists British agent B-12 and Jane his assistant in tracking down how vital military information is being to sent to Gestapo agent Lord Hawford.",NULL,"Script credit by Craig Delich verified by the Who's Who.The Arrow reveals his true identity to Jane in the story.",19
