    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23097,"You see that blond fellow speaking with Toby over there?", 1678,"Charlie tells a friend of his about Keith Butler once a star football player whose girl was kidnapped by a thug named Toby hypnotized and forced to sing in a nightclub. Keith spent five years tracking down his fiance and coming to terms with Toby.",NULL,"Stan is not credited to script on Keith Butler only the art in Jerry Bails' Who's Who.",19
