    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23181,"Green Lantern in South America", 1684,"Alan and Doiby head for South America to find out who has been tampering with Apex's short-wave outlet in Landavo and run right into a civil war caused by foreign agents seeking to overthrow the democratically-elected government. Green Lantern exposes the plot opening the eyes of the misguided rebels and saving Landavo's democracy.",NULL,NULL,19
