    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23366,"The Mystery Sub", 1705,"Dash is told by the Dean of the college that he must pass his German exam on Friday or be ineligible for the football team......just as a rado broadcast tells of the sinking of a U.S. ship by a German submarine. When he rounds up the crew of the sub the Present of the United Sates presents Dash with the Distinguished Service Cross.",NULL,"The Archives edition lists Ashe also as the writer. However the Who's Who doesn't indicate that Ashe drew this feature.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23367,"The Glass Gargoyle", 1705,"Higgins decides to go to the museum to see the famed Egyptian Buddha that has gruesome tradegies associated with it. While there a gang of crooks enter and mkae off with the exhibit which contains  a paper able to decipher an Egyptian code.",NULL,NULL,19
