    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23368,"The Submarine Signaller", 1705,"A rather unique voyage occurs the ship carrying a diplomat a condemned criminal an aspiring playright and a fading movie actress......all on the heels of a ship sunk by a German U-Boat.",NULL,"Writer confirmed by Robinson himself.",19
