    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23383,"Carnival of Death!", 1707,"Human Torch and Toro set out to solve the murder of Jimmy Mack part owner of an old vaudeville theatre years back who started a fire to collect insurance money because the show was doing poorly. Unfortunately his partner named Price was horribly burned in the fire and swore revenge. It's up to the Torch and Toro to discover the identity of the true murderer!",NULL,"Synopsis: Added by Mike Nielsen 2005-12-14 (Per Sandell ed.) revised by Craig Delich 6-28-08.  Art credits from Michael J. Vassallo via the GCD Error List (4 April 2006).",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23384,"The Strange Case of the Malay Idol", 1707,"Captain America and Bucky crash on an island in the Pacific while escorting Colonel Carter who is carrying some vital secret documents to an important meeting in Singapore. Unfortunately they are captured by a strange native tribe led by a Nazi wanting those plans!",NULL,"Synopsis: Added by Mike Nielsen 2005-12-14 (Per Sandell ed.) with revision by Craig Delich 6-28-08.  Art credits from Michael J. Vassallo via the GCD Error List (4 April 2006).",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23385,"Bombs of Doom", 1707,"The Nazis have captured Professor Berg and try to convince him to manufacture bombs for them that carry plague germs.",NULL,"Synopsis added by Craig Delich 6-28-08.",19
