    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23386,"Winners All", 1707,"Editor Stan Lee meets with current heroes featured in All-Winners and together they agreed that the Destroyer and the Whizzer should be added because of their great exploits against the Nazis.",NULL,"Illustration credit from Greg Theakston (April 19 2005). Synopsis added by Craig Delich 6-28-08.",21
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23387,"Plague of the Poisoned Jewelry", 1707,"The Nazis concoct a plan to first smuggle poisoned jewelry into the United States through a legitimate department store to reek havoc amongst the population then poison a variety of other items once people catch on to the jewelry scam.",NULL,"Synopsis added by Craig Delich 6-28-08.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23388,"The Ghost Fleet", 1707,"Sub-Mariner investigates the sinkings of Allied ships by a Nazi \"ghost fleet\" but discovers that fleet is nothing more than a movie projected in 3-D with Polaroid lenses!",NULL,"Art credits from Michael J. Vassallo via the GCD Error List (4 April 2006). Synopsis added by Craig Delich 6-28-08.",19
