    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23390,"Captain America Contents", 1708,"Illustrated table of contents page listing features some story titles & synopses and cover ad for The Young Allies Comics #1.",NULL,"Found on the inside front cover. Notes much info and synopsis added by Craig Delich 1-8-09.",25
