    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23391,"The Red Skull; An Ear for Music", 1708,"Cap has to contend with the return of the Red Skull and figure out why he is using Chopin's \"Funeral March\" as the means of his diabolical mission of destruction.",NULL,"Pencil credits from Greg Theakston. Two story titles for this story. Notes some info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23392,"Death Loads the Bases", 1708,"After the mysterious deaths of two baseball players for the Brooklyn Badgers Captain America agrees to pitch for the team in order to draw the murderer out of hiding.",NULL,"Much info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23393,"A Message from Captain America", 1708,"Captain America relates brief biographies of the various characters who appear in the pages of his magazine: Betty Ross Sergeant Duffy Father Time Jerry \"Headline\" Hunter and Hurricane.",NULL,"Most info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23394,"Horror Plays the Scales", 1708,"Cap has to battle the evil intentions of a man who uses a domestic employment agency as a blind in order to get his Nazi agents into the homes of anti-Nazi Senators in order to kill them.",NULL,"Some info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23395,"Justice Laughs Last", 1708,"Hurricane helps to fend off a group of protection racket gangsters and in the process picks himself up a new partner in the fight against crime.",NULL,"Much info and synopsis added by Craig Delich 1-8-09.Credits from Avengers Thor and Captain America:  Official Index to the Marvel Universe (Marvel 2010 series) #2.  Art formerly credited to Charles Nicholas.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23396,"Dust of Destruction", 1708,"An American man is framed for a murder of a British star and Hunter discovers that the man's cousin is responsible hoping to gain wealth from the Nazis and money from his cousin's estate after he is hanged.",NULL,"Much info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23397,"Race Against Doom", 1708,"One of Larry Scott's friends is accused of boarding a ship shooting its officers and plundering the gold before sinking it but Father Time smells a rat when he discovers the gold was counterfeit.",NULL,"Most info and synopsis added by Craig Delich 1-8-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23398,"60 Thrilling Pages of the Human Torch Fighting the Sub-Mariner in a Life and Death Struggle", 1709,NULL,NULL,NULL,6
