    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23852,"The Ship Spies", 1747,"A cutthroat pretends to be an escaped fugitive to get close to Jon Valor. Bonnie is suspicious however.",NULL,"Strip is continued in Sensation Comics #1.",19
