    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23853,"Revolution in Central America!", 1747,"On their way back from South America the Aces find themselves in the middle of a Central American revolution. A man named Miguel Miranda takes their planes for the war and the Aces have to figure out a way to get them back.",NULL,"Writer credit added by Craig Delich 9-6-08.",19
