    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(23906,"Superman versus The Archer", 1753,"A new villain hits the front pages of Metropolis' newspapers a villain who prefers hunting human beings for profit rather than animals and when Perry White cannot locate Lois or Clark to cover the story a young office boy at the Daily Planet takes on the job himself --- and gets his first by-line!",NULL,"Jimmy Olsen is blonde-haired in this story; last name Olsen not used until issue #15. This is Jimmy's first definite appearance in a comic book. First named appearance was on the Superman radio program. The Archive title for this story is \"The Archer\".",19
