    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24883,"Contents Page", 1840,"Contents page listing all the features with brief story recaps and a few illustrations.",NULL,"Found on the inside front cover. Some info added by Craig Delich 6-6-09.",25
