    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24884,"The White Death", 1840,"Cap and Bucky must tackle a white cloaked hideous figure that stalks the halls of the mansion of the late Josiah Harrow....a figure that has chosen the duo as its next target for death!",NULL,"Some info and synopsis added by Craig Delich 6-6-09.Inks:1) According to the printed credit it says Simon.2) Klein (as ghost penciller) suggested by Mitchell and Steve Dasinger. See reprints.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24885,"The Man Who Could Not Die--", 1840,"When criminal Nick Pinto was strapped into the electric chair and executed Cap and Bucky are more than a little concerned when another criminal responsible for sending Pinto to his death calls Police HQ with his last breath and tells them that Pinto has just fatally shot him.",NULL,"Ink credit from Greg Theakston. Some info and synopsis added by Craig Delich 6-6-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24886,"Death in the Alps", 1840,"Headline Hunter heads for Switzerland in response to many strange accidents that are occuring there but when he arrives he has to stop another \"strange\" accident that he gets in the middle of.",NULL,"Much info and synopsis added by Craig Delich 6-6-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24887,"Dead Man's Ring", 1840,"In years past much gold had been buried with Chief Little Eagle. Now Paul Martin having murdered the only living man who knew the location of this treasure was attempting to locate the burial spot.",NULL,"Most info and synopsis added by Craig Delich 6-6-09.",21
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24888,"The Case of the Black Talon", 1840,"Cap and Bucky have a devilish time in cornering an insane murderer who forced by fate to paint horrendous portraits decides to paint these type of paintings of the men he intends to murder!",NULL,"Ink credits from Greg Theakston. Some info and synopsis added by Craig Delich 6-6-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24889,"Crime Goes to Press", 1840,"The Master of Speed decides to get a job at \"The Daily Star\" a newspaper whose editor has been threatened and roughed up because he writes articles about the town's racketeers.",NULL,"Some info and synopsis added by Craig Delich 6-6-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24890,"The Bombs of Doom", 1840,"After having his invention (a bomb that would detonate after being near a human being for 5 minutes) turned down by the government as \"silly\" a crazed inventor decides to use that weapon against the government.",NULL,"Story title taken from the TOC. Some info and synopsis added by Craig Delich 6-6-09.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24891,NULL, 1841,NULL,NULL,NULL,6
