    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24913,"Ghost-Brought Justice", 1843,NULL,NULL,NULL,21
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24914,"The Dweller in the Haunted Cave", 1843,"Young orphan Davey Drew inadvertently frees the Demon embodiment of mankind's evil from his prison in a cave.  The Demon swears to destroy humanity but in return for freeing him he swears not to harm Davey and gives him a cloak that will protect him.  Horrified Davey brings the military to stop the Demon but succeeds only in driving him off temporarily.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24915,"The Grinner", 1843,"Dr. Weems drinks a concoction that he thinks might be a cure for pneumonia but it instead transforms him into a Mr. Hyde-like monster who becomes known as The Grinner by the local criminals.  The Black Marvel must stop him as he tries by intimidation and murder to gain control of prize oil-producing land.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24916,"Museum Robberies", 1843,"Mark Todd (in this story said to be a district attorney) hears of mysterious thefts at an art museum that happen without anyone breaking in to the building.  He decides to tackle the case as the Blazing Skull and realizes that the thief has been hiding out in the museum disguised as a statue during the day.",NULL,NULL,19
