    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(24918,"Murders By Ghost Light", 1844,"Sub-Mariner comes across an island hospital where a doctor is haunting the patients attempting to devalue the property so that he can buy it for a song and then announce that the ghost sitings were just pranks.",NULL,"Inks credits originally given to Mike Sekowsky in error (per Tony Isabella March 30 2006)",19
