    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25286,"The Play Goes To the Dogs", 1876,"Archie Betty and Jughead go around town in search of a dog for the school's production of \"Uncle Tom's Cabin\". They unlock the dog catcher's car and end up with a lot of dogs. During the play a dog spies Mrs. Grundy's hat and jumps into the audience. Chaos ensues and the Mayor falls into the \"river\" on stage.",NULL,"Mrs. Grundy shown to be married and principal of Riverdale High School.",19
