    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25790,"The Case of the Black Dragon Society", 1936,"After the mysterious suicide of a agent of the Black Dragon Society who has just killed the owner of many oil wells the Torch and Toro head east to see the owner of one of those wells discovering the Japanese plan to blow up its pipeline.",NULL,"Inks credits from Michael J. Vassallo via the GCD Error List (4 April 2006). Synopsis added by Craig Delich 6-28-08.Writer credit determined by textual analysis from Lou Mougin.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25791,"The Canvas of Doom!", 1936,"Cap and Bucky come face to face with a lunatic artist who paints portraits of his victims in the act or throes of death then invites them to view the canvas which hypnotizes them into doing away with themselves.",NULL,"Inks credit from Michale J. Vassallo via the GCD Error List (4 April 2006). The original indexer credited Bob Powell. Synopsus added by Craig Delich 6-28-08.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25792,"Terror Prison", 1936,"The Whizzer goes undercover in a prison where riots fighting and murder has occurred in order to gain inside information. But he is unaware that the appearance of a mysterious criminal called \"The Black\" is somehow tied to the prison's Warden!",NULL,"Synopsis added by Craig Delich 6-28-08.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25793,"Jungle Drums", 1936,"Greg faces a threat from Carson Wolf a notorious trader who has been trying to pressure the friendly and peaceful Senu tribe to sell him ivory at very low prices.",NULL,"Synopsis added by Craig Delich 6-28-08.",21
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25794,"The Ship of Horrors", 1936,"Namor must deal with a group of cut-throat mutineers led by Daglio who threatens the life of a ship's owner and his daughter if he refuses to turn over a secret chart which indicates the location of where a fabulously wealthy treasure ship sank.",NULL,"Synopsis added by Craig Delich 6-28-08.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25795,"The Secret Tunnel of Death!", 1936,"The Destroyer sets out to stop the Nazis led by Doctor Dragon from completing an undersea tunnel to England that will expedite the German invasion.",NULL,"Art credits from Michael J. Vassallo via the GCD Error List (4 April 2006). The original indexer credited Al Gabriele ? with the inks. Synopsis added by Craig Delich 6-28-08.",19
    );
