    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25816,"The Flaming Arrow Murders", 1939,"Torch and Toro must stop the criminal designs of The Saint who along with his gang is out to steal the plans for a new super air tank that the Raleigh Corporation is building.",NULL,NULL,19
