    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25817,"The Fox's Smuggling Racket", 1939,"Namor hears from a dock worker (aka \"brush man\") who handles fur cargoes shipped in from Alaska that no more shipments are being received and that the furs are somehow being smuggled in. Sub-Mariner investigates and discovers that The Fox is putting the stolen furs in torpedo-like metal tubes with motors and fires them into an underground river that leads to the United States.",NULL,"The Masterworks edition lists writer and inker as unknown. The last panel mentions that Sub-Mariner Comics #3 went on sale about October 1 1941.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25818,"A Business Meeting In Hell", 1939,"Satan demands that his sales manager quit falling down on the job and see to it that the only evil-free city in the United States Arbor City is turned into a hotbed of evil and misery. So he decides to send a special agent to Arbor City in the guise of a local hero to accomplish the deed.",NULL,"The Masterworks edition lists writer as unknown pencils by Al Avison and inks by George Klein.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25819,"Quick Thinking or How Good a Detective Are You?", 1939,"Ed warned Old Tom that some notorious bank robbers and killers in Kenton were headed his way and to be on the alert. They arrived and held Tom prisoner but he quickly turned the tables on the crooks.",NULL,NULL,21
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25820,"Voodini's Last Laugh From the Grave", 1939,"The relatives of the late Professor Voodini gather at his mansion to listen to the reading of the will whose contents lead to several murders and the re-appearance of the evil professor.",NULL,"The Masterworks edition lists pencil layout artist as Oksner but finished penciler and inker as unknown.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25821,"Death To Spies!", 1939,"Suoka has appointed a Filipino bolo wielder to murder Lt. Curtis but the Patriot interferes and prevents the crime finally tracking down the Japanese secret agent behind the murder plot.",NULL,"The Masterworks edition lists the writer as unknown pencils by Sid Greene and inker as unknown.",19
