    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25822,"Death Rings the Bell!", 1939,"The Angel investigates weird deaths associated with the ringing of the carillon belonging to the late millionaire Jonathan Brown.",NULL,"The Masterworks edition lists the writer and artist(s) as unknown.",19
