    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25823,"Fighting for the Free French", 1939,"Arriving in French Equatorial Africa Ka-Zar is captured by the Nazis and used as a decoy to sucker French ships into submarine range.",NULL,"Last appearance. The Masterworks edition lists the writer and inker as unknown. Writer ? added by Craig Delich 2012-8-5. The Who's Who indicates that Thompson was an artist only.",19
