    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25828,"The Monster Who Couldn't Be Stopped!!", 1940,"Don and Rusty visit the carnival and witness the brute strength of Mango the Human Muscle. Unfortunately moonlight changes this indivudal into a blood-thirsty monster who has no desire... except to kill!",NULL,"Synopsis added by Craig Delich 8-25-08.",19
