    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25829,"Quicker Than the Eye", 1940,"Magician Paul North of the All-Star Traveling Carnival is informed by the owner of a threat made against the carnival by the owner of a rival the Brite-Lite Carnival who feels the All-Star propery is hogging all the most prosperous towns to set up business in.",NULL,"Much info and synopsis added by Craig Delich 8-25-08.",21
