    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(25830,"The Deadly Pixies", 1940,"Rockman is asked by a king to save his daughter captured by the Pixies taken to their stronghold and forced to become queen of that realm and the soon-to-be bride of the Pixie King.",NULL,"Art credited previously as Syd Shores? (pencils) Vince Alascia? (inks). Revised art credits from Dr. Michael J. Vassallo 4/4/2007. Synopsis added by Craig Delich 8-25-08.",19
