    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(27171,"No One Is Safe", 2058,"Davey finds that the Demon has wrecked another town and tries again to stop him.  But he is captured and the story ends in a cliffhanger with the Demon about to drop Davey in boiling water...",NULL,"Continued next issue.",19
