    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(27172,"Hitler Meets the Destroyer", 2058,"The Destroyer takes a job as a waiter at a large hotel in Berlin in order to taunt the Nazis staying there.  He frees a British spy being tortured there which causes a Nazi manhunter named von Maus to be sent to find him.  The Destroyer defeats von Maus and uses his coat and hat as a disguise to get to Hitler whom he humiliates before escaping.",NULL,NULL,19
