    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(27173,"The Gems of Doom", 2058,"The Witness sees a jewelry store robbery and traces it back to the Imp a criminal masquerading as a law-abiding pawn shop owner.",NULL,"Tentative art credits from Dr. Michael J. Vassallo.  Jerry Bails's Who's Who also lists Bob Powell as a possible artist for this feature and associates him with the pen name S.T. Anley (Powell's birth name was Stanislav) but Dr. Vassallo disputes both the art credit and the pen name association.  Powell is not beleived to have started working for Timely until later in the decade.",19
