    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(27174,"Death Drews", 2058,"The Terror discovers that ships supposedly taking refugees from Europe to America are actually abducting them to Africa to build railroads for the Nazis as slave labor.",NULL,"The intro blurb on the splash page says that the Terror \"[threw] off the cloak of respectability\" to fight the Nazis which is not consistent with his origin three issues ago as an amnesiac used in a science experiment.Writer credit from Binder records.",19
