    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(27176,"The Thing", 2058,"A hideous man named Dr. Fear is literally scaring citizens to death with his appearance and then robbing them.  Mark Todd (in this issue described as an \"amateur criminologist\") tracks him down as the Blazing Skull and exposes his ruse.",NULL,NULL,19
