    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29618,"Invading the Nazis' Secret Plan", 2284,"Torch and Toro discover that the Nazis are operating out of a steamship anchored to a pier at Coney Island intending to deliver a bomb (along with needed tanks of oxygen) to a hospital in order to kill thousands.",NULL,"Synopsis and some info added by Craig Delich 7-3-08.",19
