    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29627,"The Imp and the Pirates", 2285,"Jefferson and the Imp set out to uncover some pirate treasure then run into a blood thirsty group of pirates determined not to let them have it.",NULL,"Contents page title; illustrations of Stan Lee and Chad Grothkopf on page 1; last appearance",19
