    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29630,"Murder in Cross Lane", 2285,"When Roddy's reporter friend Jerry Dash is accused of murdering a fellow reporter the Secret Stamp goes into action to save his life from an angry mob.",NULL,"The Masterworks edition lists Mike Sekowsky as possible penciler and Paul Reinman as possible inker but the Who's Who doesn't indicate that neither of these artists ever worked on this strip. Added by Craig Delich 2013-2-11.",19
