    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29639,"The Dynamite Saboteurs", 2287,"Von Brukk Nazi agent no.1 is using a gangster named Romo to steal dynamite in order to bomb munitions plants. Fleeing from Torch and Toro Von Brukk attempts to hide in a wind tunnel when the contraption starts up and he is ground to hamburger by the fan blades.",NULL,NULL,19
