    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29649,"Sub-Mariner Fights the Periscope Peril", 2288,"The discovery of phony submarine periscopes floating about in the ocean worries the Sub-Mariner because when this information becomes known it may hurt morale aboard ships who aren't sure whether they are real or not.",NULL,"Synopsis added by Craig Delich 3-6-08.",19
