    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29665,"The Most Amazing Story of All Time...", 2290,"Having escaped from certain death from falling from an airplane the Red Skull begins a campaign to sabotage the entire American defense effort. The Young Allies and Betty Ross confront the Skull several times unsuccessfully and finally they call in the Human Torch and Captain America to bring the battle to a sucessful conclusion.",NULL,"Continued from Captain American Comics #16. Some info and synopsis added by Craig Delich 8-28-09.",19
