    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29666,"Undiscovered Treasure", 2290,"Five of the toughest criminals alive head out to Alaska on a search for a fabulous treasure unaware that only one of them would discover the treasure alive. The ironic part is that the other four now dead discovered that treasure then threw it away!",NULL,"Most info and synopsis added by Craig Delich 8-28-09.",21
