    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(29668,NULL, 2290,"Stage magician Keith Clark demonstrates and explains how various magic tricks are done including \"The Phantom Bracelet\" \"Snake Charming\" trick and \"The Spook Coin\".",NULL,"Much info and synopsis added by Craig Delich 8-28-09.",19
