    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(31454,"Piracy on the Ocean's Bottom", 2455,"Sub-Marine looks into the mysterious sinking of U.S. naval vessels off the Eastern Coast each of them carrying a fortune in gold shipments. But he discovers a crazed doctor who has found an underground volcano that preserved the bodies of dead pirates of days gone by whom he has been able to revive by a radioactive substance so they can go out and loot ships again after they have sunk.",NULL,"Synopsis added by Craig Delich 3-8-08.",19
